dict.string:
require_once('workflows.php');
$wf = new Workflows();

$dict = array ( "fr" => "french", "en" => "english", "de" => "german", "es" => "spanish", "ru" => "russian", "pt" => "portuguese", "it" => "italian", "ja" => "japanese", "zh" => "chinese", "nl" => "dutch", "pl" => "polish", "da" => "danish", "sv" => "swedish", "fi" => "finnish", "el" => "greek", "cs" => "czech", "ro" => "romanian", "hu" => "hungarian", "sk" => "slovene", "lt" => "lithuanian", "lv" => "latvian", "et" => "estonian", "mt" => "maltese" );

$fromTo = substr("{query}", 0, 4);
$firstLangKey = substr($fromTo, 0, 2);
$secondLangKey = substr($fromTo, 2, 4);

if (array_key_exists($firstLangKey, $dict)) {
	$firstLang = $dict[$firstLangKey];
} else {
	$firstLang = "english";
}

if (array_key_exists($secondLangKey, $dict)) {
	$secondLang = $dict[$secondLangKey];
} else {
	$secondLang = "english";
}

$uppercaseFirstLang = ucwords($firstLang);
$uppercaseSecondLang = ucwords($secondLang);

$languages = urlencode( "$firstLang-$secondLang" );

$orig = substr("{query}", 4, strlen("{query}") -1);

$text = urlencode( $orig );
$xml = $wf->request( "http://google.com/complete/search?output=toolbar&q=$text" );
$xml = simplexml_load_string( utf8_encode($xml) );
$int = 1;

foreach( $xml as $sugg ):
	$data = $sugg->suggestion->attributes()->data;
	$query = "$languages/search?source=auto&query=$data";
	$wf->result( $int.'.'.time(), $query, "$data", "Translate \"$data\" from $uppercaseFirstLang to $uppercaseSecondLang in Linguee ", 'icon.png'  );
	$int++;
endforeach;

$results = $wf->results();
if ( count( $results ) == 0 ):
	$wf->result( 'googlesuggest', $orig, 'Search Linguee for '.$orig, 'No suggested matches, search Linguee for '.$orig, 'icon.png' );
endif;

echo $wf->toxml();
